import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FlightsService } from '../api/services';
import { FlightRm } from '../api/models';

@Component({
  selector: 'app-book-flight',
  templateUrl: './book-flight.component.html',
  styleUrls: ['./book-flight.component.css'],
})
export class BookFlightComponent implements OnInit {
  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private flightService: FlightsService
  ) {}

  flightId: string = 'not loaded';
  flight: FlightRm = {};

  ngOnInit(): void {
    console.log('Book Flight');
    this.route.paramMap.subscribe((p) => this.findFlight(p.get('flightId')));
  }

  private findFlight = (flightId: string | null) => {
    this.flightId = flightId ?? 'Not passed.';

    this.flightService
      .findFlights({ id: this.flightId })
      .subscribe(
        (f) => ((this.flight = f), (err: any) => this.handleError(err))
      );
  };

  private handleError = (err: any) => {
    console.log('ERROR Flight Not Found!', err.statusText);

    if (err.status == 404) {
      alert('Flight Not Found!');
      this.router.navigate(['/search-flights']);
    }
  };
}
<h5 class="text-md-center m-5">Book Flight</h5>

<div
  class="card-body text-center"
  style="padding: 20px; background-color: #e5ecf26e"
>
  <h6 class="card-title">{{ flight.airline }}</h6>
  <div class="card-title" style="color: #ff3939">
    {{ flight.numberOfPassengers }} remaining seats
  </div>
  <div class="d-grid text-center g-2">
    <div class="row">
      <div class="col">
        <div class="col m-3">
          <i
            class="text-primary fas fa-plane-departure"
            style="font-size: 20px"
          ></i>
        </div>
        <div class="col">
          <i class="fa-solid fa-location-dot">
            {{ flight.departure?.place }}
          </i>
        </div>
        <div class="col m-2">
          <i class="fa-regular"></i>
          {{ flight.departure?.time | date }}
        </div>
        <div class="col">
          <i class="far fa-clock">
            {{ flight.departure?.time | date : "shortTime" }}
          </i>
        </div>
      </div>

      <div class="col">
        <div style="display: flex">
          <i
            class="fas fa-circle"
            style="margin-top: 51px; font-size: 9px"
          ></i>
          <hr style="display: block; width: 100%; margin-top: 55px" />
          <i class="fas fa-plane" style="margin-top: 48px"></i>
        </div>
        <i class="fa fa-dollar-sign" aria-hidden="true">{{ flight.price }}</i>
        <br />
      </div>

      <div class="col">
        <div class="col m-3">
          <i
            class="text-primary fas fa-plane-arrival"
            style="font-size: 20px"
          ></i>
        </div>
        <div class="col">
          <i class="fas fa-location-dot">
            {{ flight.arrival?.place }}
          </i>
        </div>
        <div class="col m-2 text-primary">
          <i class="fa-regular"></i>
          {{ flight.arrival?.time | date }}
        </div>
        <div class="col">
          <i class="far fa-clock">
            {{ flight.arrival?.time | date : "shortTime" }}
          </i>
        </div>
      </div>
    </div>
  </div>
</div>

<div class="card-footer d-flex justify-content-center">
  <label class="form-label mt-3" for="numberOfSeats">Number of Seats</label>
  <div class="mt-3 center input-group">
    <input
      type="number"
      class="form-control"
      id="numberOfSeats"
      name="numberOfSeats"
      placeholder="Number of Seats"
    />
    <button type="submit" class="btn btn-primary">
      <i class="fas fa-ticket"></i>
      Book
    </button>
  </div>
  <br />
</div>
